import React, { useState, useEffect } from 'react';
import Hls from 'hls.js';

const VideoPlayer = () => {
  const [overlays, setOverlays] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [overlayText, setOverlayText] = useState("");
  const [isMounted, setIsMounted] = useState(true);

  useEffect(() => {
    setIsMounted(true);
    fetchOverlays();

    // Video player setup
    const video = document.getElementById('video');
    if (Hls.isSupported()) {
      const hls = new Hls();
      hls.loadSource('http://127.0.0.1:5000/hls/stream.m3u8');
      hls.attachMedia(video);
    } else if (video.canPlayType('application/vnd.apple.mpegurl')) {
      video.src = 'http://127.0.0.1:5000/hls/stream.m3u8';
    }

    return () => {
      setIsMounted(false);
    };
  }, []); // Runs once on mount

  // Fetch overlays from backend
  const fetchOverlays = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5000/api/overlays');
      const data = await response.json();
      if (Array.isArray(data) && isMounted) {
        setOverlays(data);
      }
    } catch (error) {
      console.error('Error fetching overlays:', error);
    }
  };

  // Handle play/pause
  const handlePlayPause = () => {
    const video = document.getElementById('video');
    if (isPlaying) {
      video.pause();
    } else {
      video.play().catch((error) => {
        console.error("Error trying to play the video:", error);
      });
    }
    setIsPlaying(!isPlaying);
  };

  // Handle creating a new overlay
  const handleCreateOverlay = async () => {
    const newOverlay = {
      text: overlayText,
      position: { top: 50, left: 50 },
      color: "#000000", // Default color
      opacity: 1,       // Default opacity
    };

    try {
      await fetch('http://127.0.0.1:5000/api/overlays', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newOverlay),
      });
      setOverlays([...overlays, newOverlay]);
      setOverlayText(""); // Clear the input field
    } catch (error) {
      console.error('Error creating overlay:', error);
    }
  };

  // Handle editing an overlay
  const handleEditOverlay = async (overlayId) => {
    const updatedOverlay = {
      text: prompt("Enter new text for overlay:"),
      position: { top: 100, left: 100 },
      color: prompt("Enter overlay color:", "#000000"),
      opacity: prompt("Enter overlay opacity (0-1):", "1"),
    };

    try {
      await fetch(`http://127.0.0.1:5000/api/overlays/${overlayId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updatedOverlay),
      });

      setOverlays(prevOverlays => prevOverlays.map(overlay => 
        overlay._id === overlayId ? { ...overlay, ...updatedOverlay } : overlay
      ));
    } catch (error) {
      console.error('Error editing overlay:', error);
    }
  };

  // Handle deleting an overlay
  const handleDeleteOverlay = async (overlayId) => {
    try {
      await fetch(`http://127.0.0.1:5000/api/overlays/${overlayId}`, {
        method: 'DELETE',
      });

      setOverlays(overlays.filter(overlay => overlay._id !== overlayId));
    } catch (error) {
      console.error('Error deleting overlay:', error);
    }
  };

  // Handle overlay dragging
  const handleDrag = (e, overlayId) => {
    const updatedOverlays = overlays.map(overlay => {
      if (overlay._id === overlayId) {
        return {
          ...overlay,
          position: {
            top: e.clientY - 50, // Adjust to keep the overlay centered around the cursor
            left: e.clientX - 50,
          },
        };
      }
      return overlay;
    });
    setOverlays(updatedOverlays);
  };

  // Render overlays on video
  const renderOverlays = () => {
    return overlays.map((overlay) => (
      <div 
        key={overlay._id} 
        className="overlay" 
        style={{
          position: 'absolute',
          top: overlay.position?.top || '50px',
          left: overlay.position?.left || '50px',
          backgroundColor: overlay.color,
          opacity: overlay.opacity,
          zIndex: 999
        }}
        onMouseDown={(e) => e.preventDefault()}
        onMouseMove={(e) => handleDrag(e, overlay._id)}
      >
        {overlay.text}
        <div className="resize-handle"></div>
        <button onClick={() => handleDeleteOverlay(overlay._id)}>Delete</button>
        <button onClick={() => handleEditOverlay(overlay._id)}>Edit</button>
      </div>
    ));
  };

  return (
    <div className="video-player-container">
      <h1>Livestream</h1>
      <video id="video" controls width="800" className="video-element">
        Your browser does not support the video tag.
      </video>

      <div className="control-buttons">
        <button onClick={handlePlayPause} className="play-pause-button">
          {isPlaying ? 'Pause' : 'Play'} Video
        </button>
      </div>

      <div className="overlay-input">
        <input 
          type="text" 
          value={overlayText} 
          onChange={(e) => setOverlayText(e.target.value)} 
          placeholder="Enter overlay text"
          className="overlay-text-input"
        />
        <button onClick={handleCreateOverlay} className="add-overlay-button">
          Add Overlay
        </button>
      </div>

      <div className="overlays-container">
        {renderOverlays()}
      </div>
    </div>
  );
};

export default VideoPlayer;
