import React, { useState, useEffect } from 'react';
import Hls from 'hls.js';

const VideoPlayer = () => {
  const [overlays, setOverlays] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [hls, setHls] = useState(null);  // Keep track of the HLS instance

  useEffect(() => {
    // Fetch overlays from the backend
    fetch('http://127.0.0.1:5000/api/overlays')
      .then(response => response.json())
      .then(data => {
        if (Array.isArray(data)) {
          setOverlays(data);  // Ensure overlays is an array
        } else {
          console.error('Invalid overlays data:', data);
        }
      })
      .catch(error => console.error('Error fetching overlays:', error));

    const video = document.getElementById('video');

    // Initialize HLS.js if supported
    if (Hls.isSupported()) {
      const hlsInstance = new Hls();
      hlsInstance.loadSource('http://127.0.0.1:5000/hls/stream.m3u8');
      hlsInstance.attachMedia(video);
      hlsInstance.on(Hls.Events.MANIFEST_PARSED, () => {
        document.getElementById('playButton').addEventListener('click', () => {
          if (isPlaying) {
            video.pause();
          } else {
            video.play();
          }
          setIsPlaying(!isPlaying);
        });
      });

      // Save the HLS instance to avoid reloading the stream
      setHls(hlsInstance);
    } else if (video.canPlayType('application/vnd.apple.mpegurl')) {
      // Safari browser
      video.src = 'http://127.0.0.1:5000/hls/stream.m3u8';
      video.addEventListener('loadedmetadata', () => {
        document.getElementById('playButton').addEventListener('click', () => {
          if (isPlaying) {
            video.pause();
          } else {
            video.play();
          }
          setIsPlaying(!isPlaying);
        });
      });
    }

    // Cleanup HLS instance on component unmount
    return () => {
      if (hls) {
        hls.destroy();
      }
    };
  }, [isPlaying, hls]);

  // Function to render overlays
  const renderOverlays = () => {
    if (!Array.isArray(overlays)) return null;

    return overlays.map((overlay, index) => (
      <div
        key={index}
        className="overlay"
        style={{
          top: overlay.position?.top || '50px',
          left: overlay.position?.left || '50px',
        }}
      >
        {overlay.text}
        <div className="resize-handle"></div>
      </div>
    ));
  };

  return (
    <div>
      <h1>Livestream</h1>
      <video id="video" controls width="600">
        Your browser does not support the video tag.
      </video>
      <button id="playButton">{isPlaying ? 'Pause' : 'Play'} Video</button>
      {renderOverlays()}
    </div>
  );
};

export default VideoPlayer;
