import React, { useEffect } from 'react';
import Hls from 'hls.js';

const VideoPlayer = () => {
  useEffect(() => {
    const video = document.getElementById('livestream');
    
    if (Hls.isSupported()) {
      const hls = new Hls();
      hls.loadSource('http://localhost:8000/static/hls/stream.m3u8');  // Link to your m3u8 stream
      hls.attachMedia(video);
      hls.on(Hls.Events.MANIFEST_PARSED, () => {
        video.play();
      });
    } else if (video.canPlayType('application/vnd.apple.mpegurl')) {
      video.src = 'http://localhost:8000/static/hls/stream.m3u8';  // For Safari
      video.addEventListener('loadedmetadata', () => {
        video.play();
      });
    }
  }, []);

  return (
    <div>
      <h1>Livestream</h1>
      <video id="livestream" controls width="600">
        Your browser does not support the video tag.
      </video>
    </div>
  );
};

export default VideoPlayer;
