import React, { useState, useEffect, useCallback } from 'react';
import Hls from 'hls.js';

const VideoPlayer = () => {
  const [overlays, setOverlays] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false); // To track play/pause state

  // Use useCallback to memoize the function to avoid unnecessary re-creations
const handlePlayPause = () => {
  const video = document.getElementById('video');
  
  if (isPlaying) {
    video.pause();
  } else {
    video.play().catch((error) => {
      console.error("Error trying to play the video:", error);
    });
  }
  setIsPlaying(!isPlaying);
};


  useEffect(() => {
    // Fetch overlays from the backend
    fetch('http://127.0.0.1:5000/api/overlays')
      .then(response => response.json())
      .then(data => {
        if (Array.isArray(data)) {
          setOverlays(data);  // Ensure overlays is an array
        } else {
          console.error('Invalid overlays data:', data);
        }
      })
      .catch(error => console.error('Error fetching overlays:', error));

    const video = document.getElementById('video');
    
    // Initialize HLS.js if supported
    if (Hls.isSupported()) {
      const hls = new Hls();
      hls.loadSource('http://127.0.0.1:5000/hls/stream.m3u8'); // RTSP to HLS URL
      hls.attachMedia(video);
    } else if (video.canPlayType('application/vnd.apple.mpegurl')) {
      video.src = 'http://127.0.0.1:5000/hls/stream.m3u8'; // RTSP to HLS URL
    }

    // Cleanup function to remove any event listeners when the component unmounts
    return () => {
      const playButton = document.getElementById('playButton');
      if (playButton) {
        playButton.removeEventListener('click', handlePlayPause);
      }
    };
  }, [handlePlayPause]); // Now handlePlayPause is a dependency

  // Function to render overlays
  const renderOverlays = () => {
    if (!Array.isArray(overlays)) return null;

    return overlays.map((overlay, index) => (
      <div
        key={index}
        className="overlay"
        style={{
          top: overlay.position?.top || '50px',
          left: overlay.position?.left || '50px',
        }}
      >
        {overlay.text}
        <div className="resize-handle"></div>
      </div>
    ));
  };

  return (
    <div>
      <h1>Livestream</h1>
      <video id="video" controls width="600">
        Your browser does not support the video tag.
      </video>
      <button id="playButton" onClick={handlePlayPause}>
        {isPlaying ? 'Pause' : 'Play'} Video
      </button>
      {renderOverlays()}
    </div>
  );
};

export default VideoPlayer;
